INSERT [dbo].[LPK] ([LPKId], [Note], [ContactTypeEID], [TypeServiceEID], [ClientFromEID], [SexEID], [AgeEID], [Nick], [CallId], [ContactTopicEID]) VALUES (165, N'Volá M, který má skoro 80 letou matku, bydlí sama v domě. Nyní mu začala povídat, že k ní chodí nějaká sociální pracovnice, která jí pomáhá a zařizuje domov důchodců. Jeho to zarazilo, matka má stařeckou demenci, ale zatím je doma soběstačná. Ptal se jí na jméno oné sociální pracovnice. Matka nevěděla, že vždy přijde sama od sebe si popovídat. M si to chtěl ověřit i u lékaře, ale máma mu nechce dát kontakt. Bojí se o ní, aby to nebyla nějaká podvodnice. Chápu a je dobře, že hledá pomoc. M napadlo zajít na policii, aby to ověřila. Souhlasím, že určitě může zajít na nejbližší služebnu a poradit se, jestli podat oznámení. M má strach, aby máma něco nevědomky té cizí osobě nepodepsala. Rozumím. Určitě je dobře zjistit, jak to je. M děkuje a půjde se poradit na oddělení policie.', 1, 1, 3, 1, 5, N'', 1319, NULL)
